import numpy as npimport pandas as pdimport seaborn as snsimport matplotlib.pyplot as pltimport matplotlib.ticker# ігноруємо warningsimport warningswarnings.filterwarnings("ignore")sns.set_context( "notebook", font_scale = 1.5, rc = { "figure.figsize" : (12, 9), "axes.titlesize" : 18 })df = pd.read_csv('testdataKursova.csv', sep=';', index_col='id')df_uniques = pd.melt(frame=df, value_vars=['denTuznyaKoluPruhodutKlient'])df_uniques = pd.DataFrame(df_uniques.groupby(['variable','value'])['value'].count())\ .sort_index(level=[0, 1]) \ .rename(columns={'value': 'count'}) \ .reset_index()sns.factorplot(x='variable', y='count', hue='value', data=df_uniques, kind='bar', size=12)plt.show()